import useInput from "../hooks/use-input";

const BasicForm = (props) => {

  const {reset, isUserInvalid : isNameInvalid, valueChangeHandler : nameChangeHandler, blurHandler:nameBlurHandler} = useInput(value => value.trim() === '');

  let formIsValid = false;

  if(isNameInvalid){
    formIsValid = true;
  }

  const submitHandler = () => {
    reset();
  }

  const formClass = formIsValid ? 'form-control' : 'form-control invalid';


  return (
    <form onSubmit={submitHandler}>
      <div className='control-group'>
        <div className={formClass}>
          <label htmlFor='name'>First Name</label>
          <input type='text' id='name' onChange={nameChangeHandler} onBlur={nameBlurHandler}/>
          {isNameInvalid && <p>Name is not valid.</p>}
        </div>
        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' />
        </div>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' />
      </div>
      <div className='form-actions'>
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
